import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import { 
  Box, 
  Paper, 
  TextField, 
  Button, 
  Typography, 
  CircularProgress,
  IconButton,
  Divider
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import DeleteIcon from '@mui/icons-material/Delete';
import MessageList from './MessageList';

const ChatUI = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const messagesEndRef = useRef(null);

  // Scroll to bottom when messages change
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Auto-greet on first load
  useEffect(() => {
    if (messages.length === 0) {
      handleSubmit('', true);
    }
  }, []);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = async (inputValue = input, isNewConversation = false) => {
    // Use provided input value or the current state
    const userMessage = inputValue || input;

    // Don't send empty messages (except for initial greeting)
    if (!userMessage && !isNewConversation) return;

    try {
      // Update UI immediately with user message if it's not the initial greeting
      if (!isNewConversation) {
        setMessages(prev => [...prev, { role: 'user', content: userMessage }]);
        setInput('');
      }

      // Create the messages payload
      const payload = {
        messages: isNewConversation ? [] : [...messages, { role: 'user', content: userMessage }],
        isNewConversation: isNewConversation
      };

      // Show loading state
      setLoading(true);
      setError(null);

      // Call the API
      const response = await axios.post('/api/chat', payload);

      // Update conversation with the assistant's response
      if (isNewConversation) {
        // For a new conversation, we only need the assistant's greeting
        setMessages([{ role: 'assistant', content: response.data.message }]);
      } else {
        // For ongoing conversations, use the full conversation returned from the API
        setMessages(response.data.conversation);
      }
    } catch (err) {
      console.error('Error communicating with the chatbot:', err);
      setError('Sorry, there was an error communicating with the chatbot. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit();
    }
  };

  const handleClearChat = () => {
    setMessages([]);
    // Auto-greet again after clearing
    setTimeout(() => {
      handleSubmit('', true);
    }, 100);
  };

  return (
    <Paper 
      elevation={3} 
      sx={{ 
        height: '100%', 
        display: 'flex', 
        flexDirection: 'column',
        overflow: 'hidden',
        backgroundColor: '#fff',
      }}
    >
      <Box sx={{ 
        p: 2, 
        backgroundColor: '#f5f9fd', 
        display: 'flex', 
        justifyContent: 'space-between',
        alignItems: 'center',
        borderBottom: '1px solid #e0e0e0'
      }}>
        <Typography variant="h6" color="primary">
          Chat with DompeAssist
        </Typography>
        <IconButton 
          color="primary" 
          onClick={handleClearChat}
          size="small"
          title="Start new conversation"
        >
          <DeleteIcon />
        </IconButton>
      </Box>

      <Box sx={{ 
        flex: 1, 
        overflow: 'auto', 
        p: 2,
        display: 'flex',
        flexDirection: 'column',
      }}>
        <MessageList messages={messages} />

        {loading && (
          <Box sx={{ display: 'flex', justifyContent: 'center', p: 2 }}>
            <CircularProgress size={24} />
          </Box>
        )}

        {error && (
          <Paper 
            sx={{ 
              p: 2, 
              mb: 2, 
              backgroundColor: '#fdeaea',
              borderLeft: '4px solid #f44336'
            }}
          >
            <Typography color="error">{error}</Typography>
          </Paper>
        )}

        <div ref={messagesEndRef} />
      </Box>

      <Divider />

      <Box sx={{ 
        p: 2, 
        display: 'flex', 
        backgroundColor: '#f5f9fd',
      }}>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Type your message..."
          value={input}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          disabled={loading}
          multiline
          maxRows={4}
          size="small"
          sx={{ 
            mr: 1,
            '& .MuiOutlinedInput-root': {
              borderRadius: '20px',
              backgroundColor: '#ffffff',
            }
          }}
        />
        <Button
          variant="contained"
          color="primary"
          disableElevation
          disabled={loading || (!input.trim() && messages.length > 0)}
          onClick={() => handleSubmit()}
          endIcon={<SendIcon />}
          sx={{ borderRadius: '20px', px: 3 }}
        >
          Send
        </Button>
      </Box>
    </Paper>
  );
};

export default ChatUI;
