import React from 'react';
import { Box, Paper, Typography, Link, Divider } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';
import LaunchIcon from '@mui/icons-material/Launch';

const SearchResultDisplay = ({ result }) => {
  if (!result) return null;

  return (
    <Paper
      elevation={1}
      sx={{
        p: 2,
        mb: 3,
        backgroundColor: '#f8f9fa',
        borderLeft: '4px solid #1976d2',
        maxWidth: '100%',
      }}
    >
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
        <SearchIcon color="primary" sx={{ mr: 1 }} />
        <Typography variant="subtitle1" fontWeight="medium" color="primary">
          Web Search Results
        </Typography>
      </Box>

      <Divider sx={{ mb: 2 }} />

      {result.abstractText && (
        <Box sx={{ mb: 2 }}>
          <Typography variant="body2" sx={{ mb: 0.5 }}>
            {result.abstractText}
          </Typography>

          {result.abstractURL && (
            <Link 
              href={result.abstractURL} 
              target="_blank" 
              rel="noopener noreferrer"
              sx={{ 
                display: 'flex', 
                alignItems: 'center',
                fontSize: '0.875rem',
                mt: 0.5 
              }}
            >
              Source: {result.source}
              <LaunchIcon sx={{ ml: 0.5, fontSize: '0.875rem' }} />
            </Link>
          )}
        </Box>
      )}

      {result.relatedTopics && result.relatedTopics.length > 0 && (
        <Box>
          <Typography variant="body2" fontWeight="medium" sx={{ mb: 1 }}>
            Related Information:
          </Typography>

          {result.relatedTopics.map((topic, index) => (
            <Box key={index} sx={{ mb: 1, pl: 1, borderLeft: '2px solid #e0e0e0' }}>
              <Typography variant="body2" sx={{ mb: 0.5 }}>
                {topic.text}
              </Typography>

              {topic.url && (
                <Link 
                  href={topic.url} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  sx={{ 
                    display: 'flex', 
                    alignItems: 'center',
                    fontSize: '0.875rem' 
                  }}
                >
                  Read more
                  <LaunchIcon sx={{ ml: 0.5, fontSize: '0.875rem' }} />
                </Link>
              )}
            </Box>
          ))}
        </Box>
      )}

      <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 1 }}>
        Information sourced from {result.source || 'the web'}
      </Typography>
    </Paper>
  );
};

export default SearchResultDisplay;
