import React, { useRef, useEffect } from 'react';
import { Box, Avatar, Paper, Typography } from '@mui/material';
import ReactMarkdown from 'react-markdown';
import SupportAgentIcon from '@mui/icons-material/SupportAgent';
import PersonIcon from '@mui/icons-material/Person';

const MessageList = ({ messages, renderMessage }) => {
  const messagesEndRef = useRef(null);

  // Scroll to the bottom whenever messages change
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  return (
    <Box sx={{ 
      flex: 1, 
      overflowY: 'auto', 
      p: 2, 
      display: 'flex', 
      flexDirection: 'column',
      gap: 1
    }}>
      {messages.map((message, index) => (
        renderMessage ? renderMessage(message, index) : (
          <Box
            key={index}
            sx={{
              display: 'flex',
              justifyContent: message.role === 'user' ? 'flex-end' : 'flex-start',
              mb: 2
            }}
          >
            <Box
              sx={{
                maxWidth: '70%',
                p: 2,
                borderRadius: 2,
                bgcolor: message.role === 'user' ? '#e3f2fd' : '#ffffff',
                boxShadow: 1
              }}
            >
              {message.content}
            </Box>
          </Box>
        )
      ))}
      <div ref={messagesEndRef} />
    </Box>
  );
};

const Message = ({ message }) => {
  const isUser = message.role === 'user';

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: isUser ? 'flex-end' : 'flex-start',
        mb: 2,
      }}
    >
      <Box
        sx={{
          display: 'flex',
          flexDirection: isUser ? 'row' : 'row',
          alignItems: 'start',
          maxWidth: '80%',
        }}
      >
        {!isUser && (
          <Avatar
            sx={{
              bgcolor: 'primary.main',
              mr: 1,
              width: 36,
              height: 36,
            }}
          >
            <SupportAgentIcon fontSize="small" />
          </Avatar>
        )}

        <Paper
          elevation={1}
          sx={{
            p: 2,
            bgcolor: isUser ? 'primary.main' : 'background.paper',
            color: isUser ? 'white' : 'text.primary',
            borderRadius: isUser ? '15px 15px 0 15px' : '15px 15px 15px 0',
            border: isUser ? 'none' : '1px solid #e0e0e0',
            boxShadow: isUser 
              ? '0 2px 5px rgba(0, 99, 163, 0.2)' 
              : '0 2px 5px rgba(0, 0, 0, 0.05)',
          }}
        >
          <Typography
            component="div"
            sx={{
              '& p': {
                m: 0,
              },
              '& a': {
                color: isUser ? 'inherit' : 'primary.main',
                textDecoration: 'underline',
              },
              '& ul, & ol': {
                pl: 2.5,
                mb: 1,
              },
              '& code': {
                bgcolor: isUser ? 'rgba(255,255,255,0.15)' : 'rgba(0,0,0,0.04)',
                p: 0.5,
                borderRadius: 1,
                fontFamily: 'monospace',
              },
              '& pre': {
                bgcolor: isUser ? 'rgba(255,255,255,0.15)' : 'rgba(0,0,0,0.04)',
                p: 1,
                borderRadius: 1,
                overflow: 'auto',
                maxWidth: '100%',
              },
              '& strong': {
                fontWeight: 'bold',
              },
              '& h1, & h2, & h3, & h4, & h5, & h6': {
                my: 1,
              },
            }}
          >
            <ReactMarkdown>{message.content}</ReactMarkdown>
          </Typography>
        </Paper>

        {isUser && (
          <Avatar
            sx={{
              bgcolor: 'secondary.main',
              ml: 1,
              width: 36,
              height: 36,
            }}
          >
            <PersonIcon fontSize="small" />
          </Avatar>
        )}
      </Box>
    </Box>
  );
};

export default MessageList;
